import React, { useState } from "react";
import "./Login.css";
import Logo from "../../assets/text-to-query-logo.png"; // Ensure this file exists

const Login = ({ handleLogin, switchToSignup, skipLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();
    handleLogin(username, password);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <img src={Logo} alt="Text to Query Logo" />
        <h2>Welcome Back</h2>
        <form onSubmit={onSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Log In</button>
        </form>
        <p>
          New here? <span onClick={switchToSignup}>Sign Up</span>
        </p>
        <button className="skip-button" onClick={skipLogin}>
          Continue Without Login
        </button>
      </div>
    </div>
  );
};

export default Login;
