import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import ChatBox from "./components/ChatBox";
import ChatInput from "./components/ChatInput";
import Signup from "./components/Signup/Signup";
import Login from "./components/Login/Login";
import axios from "axios";
import "./App.css";
import { jwtDecode } from "jwt-decode";

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [employeeDetails, setEmployeeDetails] = useState(null);
  useEffect(() => {
    const fetchEmployeeDetails = async () => {
      const token = localStorage.getItem("jwtToken");
      if (token) {
        try {
          const decoded = jwtDecode(token);
          const email = decoded.email;
          const response = await axios.post(
            "http://localhost:9000/employees/details", 
            { email },
            {
              headers: {
                Authorization: `Bearer ${token}`, // Add the token to the Authorization header
              },
            }
          );
          setEmployeeDetails(response.data);
          console.log(employeeDetails)
        } catch (error) {
          console.error("Error fetching employee details:", error);
        }
      }
    };

    fetchEmployeeDetails();
  }, []);

  const generateTable = (results) => {
    if (!results || results.length === 0) return "No results found.";

    const headers = Object.keys(results[0]);

    const rows = results.map((result) => {
      const cells = headers.map((header) => `<td>${result[header]}</td>`).join("");
      return `<tr>${cells}</tr>`;
    });

    const table = `
      <table border="1" style="border-collapse: collapse; width: 100%;">
        <thead>
          <tr>${headers.map((header) => `<th>${header}</th>`).join("")}</tr>
        </thead>
        <tbody>
          ${rows.join("")}
        </tbody>
      </table>
    `;
    return table;
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    setMessages([...messages, { sender: "user", text: input }]);
    try {
      const response = await axios.post("http://localhost:5000/textToQuery", { text: input });
      if (response.data.error) {
        throw new Error(response.data.error);
      }

      const query = response.data.query;
      const results = response.data.results;
      const table = generateTable(results);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "bot", text: `Generated Query:\n${query}` },
        {
          sender: "bot",
          text: table,
          isTable: true,
          results,
        },
      ]);
      setInput("");
    } catch (error) {
      const errorMessage =
        error.response && error.response.data && error.response.data.error
          ? error.response.data.error
          : error.message || "Something went wrong.";

      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "bot", text: `Error: ${errorMessage}` },
      ]);
      setInput("");
    }
  };
  const handleLogout=()=>
  {
    localStorage.removeItem("jwtToken")
    setIsLoggedIn(false)
  }


  return (
    <Router>
      <div className="app-container">
        <Routes>
          <Route
            path="/signup"
            element={<Signup/>}
          />
          <Route
            path="/login"
            element={
              isLoggedIn ? <Navigate to="/" /> : <Login setIsLoggedIn={setIsLoggedIn} />
            }
          />
          <Route
            path="/"
            element={
              isLoggedIn ? (
                <>
                  <Navbar  handleLogout={handleLogout}/>
                  <div className="chat-container">
                    <ChatBox messages={messages} />
                    <ChatInput input={input} setInput={setInput} handleSend={handleSend} />
                  </div>
                </>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
