import React, { useState } from "react";
import "./Signup.css";
import Logo from "../../assets/text-to-query-logo.png"; // Add a logo file in assets

const Signup = ({ handleSignup, switchToLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const validatePassword = (password) => {
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d]{8,20}$/;
    return passwordRegex.test(password);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if (!validatePassword(password)) {
      setError(
        "Password must be 8-20 characters long, include at least one uppercase letter, one lowercase letter, and one number."
      );
      return;
    }
    handleSignup(username, password);
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <img src={Logo} alt="Text to Query Logo" />
        <h2>Create Account</h2>
        <form onSubmit={onSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            {error && <p className="error">{error}</p>}
          </div>
          <button type="submit">Sign Up</button>
        </form>
        <p>
          Already have an account? <span onClick={switchToLogin}>Log In</span>
        </p>
      </div>
    </div>
  );
};

export default Signup;
