import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";
import Logo from "../../assets/text-to-query-logo.png"; // Ensure this file exists

const Login = ({ setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const onSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous error messages
    try {
      const response = await axios.post("http://localhost:9000/employees/login", {
        email,
        password,
      });

      if (response.status === 200 && response.data.token) {
        const { token } = response.data;
        console.log("Token received:", token);

        localStorage.setItem("jwtToken", token);
        setIsLoggedIn(true);
        navigate("/"); 
      } else {
        // Handle unexpected response
        setError("Unexpected response from the server. Please try again.");
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || "Invalid credentials. Please try again.";
      setError(errorMessage);
    }
  };


  return (
    <div className="login-container">
      <div className="login-card">
        <img src={Logo} alt="Text to Query Logo" />
        <h2>Welcome Back</h2>
        <form onSubmit={onSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {error && <p className="error-message">{error}</p>}
          <button type="submit">Log In</button>
        </form>
        <p>
          New here? <Link to="/signup">Sign Up</Link>
        </p>
        <button className="skip-button" onClick={() => navigate("/")}>
          Continue Without Login
        </button>
      </div>
    </div>
  );
};

export default Login;
