import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import ChatBox from "./components/ChatBox";
import ChatInput from "./components/ChatInput";
import Signup from "./components/Signup/Signup";
import Login from "./components/Login/Login";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import "./App.css";
const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [employeeDetails, setEmployeeDetails] = useState(null);

  const fetchEmployeeDetails = async () => {
    const token = localStorage.getItem("jwtToken");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        const email = decoded.email;
        const response = await axios.get(
          `http://localhost:9000/employees/empDetailsByEmail?email=${email}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setEmployeeDetails(response.data);
        console.log("Employee Details Updated:", response.data);
      } catch (error) {
        console.error("Error fetching employee details:", error);
      }
    }
  };

  useEffect(() => {
    fetchEmployeeDetails(); // Fetch details on initial render if the user is already logged in
  }, []);

  const handleSend = async () => {
    if (!input.trim()) return;
    setMessages([...messages, { sender: "user", text: input }]);
    try {
      const response = await axios.post("http://localhost:5000/textToQuery", { text: input });
      if (response.data.error) {
        throw new Error(response.data.error);
      }

      const query = response.data.query;
      const results = response.data.results;
      const table = generateTable(results);
      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "bot", text: `Generated Query:\n${query}` },
        {
          sender: "bot",
          text: table,
          isTable: true,
          results,
        },
      ]);
      const id = employeeDetails?.id;
      console.log("Employee ID:", id);
      setInput("");
    } catch (error) {
      const errorMessage =
        error.response && error.response.data && error.response.data.error
          ? error.response.data.error
          : error.message || "Something went wrong.";

      setMessages((prevMessages) => [
        ...prevMessages,
        { sender: "bot", text: `Error: ${errorMessage}` },
      ]);
      setInput("");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("jwtToken");
    setIsLoggedIn(false);
    setEmployeeDetails(null); // Clear employee details on logout
  };

  return (
    <Router>
      <div className="app-container">
        <Routes>
          <Route path="/signup" element={<Signup />} />
          <Route
            path="/login"
            element={
              isLoggedIn ? (
                <Navigate to="/" />
              ) : (
                <Login
                  setIsLoggedIn={setIsLoggedIn}
                  fetchEmployeeDetails={fetchEmployeeDetails} // Pass fetch function
                />
              )
            }
          />
          <Route
            path="/"
            element={
              isLoggedIn ? (
                <>
                  <Navbar handleLogout={handleLogout} />
                  <div className="chat-container">
                    <ChatBox messages={messages} />
                    <ChatInput input={input} setInput={setInput} handleSend={handleSend} />
                  </div>
                </>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
